mapper.insert:
INSERT INTO tb_comment (

) VALUES (
    #{id}
    , #{boardId}
    , #{content}
    , #{author}
    , IFNULL(#{deleteYn}, 'N')
    , NOW()
    , NULL
    , NULL
)

mapper.update:
UPDATE tb_comment
SET
    update_time = NOW()
  , content = #{content}
  , author = #{author}
WHERE
    id = #{id}
